drop table if exists item cascade;
drop table if exists regions cascade;

CREATE CACHED TABLE "PUBLIC"."ITEM"(
    "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL,
    "DELIVERYCODE" CHARACTER VARYING(255),
    "ITEMNAME" CHARACTER VARYING(255),
    "ITEMTYPE" CHARACTER VARYING(255),
    "OPEN" BOOLEAN,
    "PRICE" INTEGER,
    "QUANTITY" INTEGER
);

ALTER TABLE "PUBLIC"."ITEM" ADD CONSTRAINT "PUBLIC"."PK_ITEM_ID" PRIMARY KEY("ID");

CREATE CACHED TABLE "PUBLIC"."REGIONS"(
    "ITEM_ID" BIGINT NOT NULL,
    "REGIONS" CHARACTER VARYING(255)
);

ALTER TABLE "PUBLIC"."REGIONS" ADD CONSTRAINT "PUBLIC"."FK_REGIONS_ITEM_ID" FOREIGN KEY("ITEM_ID") REFERENCES "PUBLIC"."ITEM"("ID") NOCHECK;
